mapper.update:
update user_session
set jti        = #{newJti},
    issued_at  = #{issuedAt},
    expires_at = #{expiresAt},
    ip         = #{ip},
    user_agent = #{userAgent}
where jti = #{oldJti}
  and device_id = #{deviceId}
  AND platform = #{platform}
  and revoked = 0
  and expires_at > now(3)
